INSERT INTO tasks (duration, title, guidance, category, steps, active) VALUES
  (1, 'Respiración cuadrada x3', 'Inhala 4, retén 4, exhala 4, retén 4. Repite tres ciclos notando cómo se calma tu ritmo.', 'Respiración & calma', NULL, 1),
  (1, 'Exhala más largo (4–6)', 'Respira suave: inhala 4 y exhala 6 durante un minuto para soltar tensión.', 'Respiración & calma', NULL, 1),
  (1, 'Cuenta 10 respiraciones', 'Cuenta mentalmente de 1 a 10 cada respiración; si te distraes, vuelve a 1 con paciencia.', 'Respiración & calma', NULL, 1),
  (1, 'Manos en vientre y pecho', 'Coloca una mano en el vientre y otra en el pecho y siente el movimiento del aire al entrar y salir.', 'Respiración & calma', NULL, 1),
  (1, 'Suspiro doble x3', 'Haz un suspiro doble por la nariz (corto+corto) y exhala largo por la boca; repite tres veces.', 'Respiración & calma', NULL, 1),
  (1, '“Aquí” al exhalar', 'En cada exhalación di en silencio “aquí”, reconectando con lo que estás viviendo ahora.', 'Respiración & calma', NULL, 1),
  (1, '3-2-1 sentidos', 'Nombra 3 cosas que ves, 2 que oyes y 1 que sientes en la piel.', 'Sentidos al presente', NULL, 1),
  (1, 'Luces y sombras', 'Observa las luces y sombras a tu alrededor sin juzgar, solo reconociendo formas.', 'Sentidos al presente', NULL, 1),
  (1, 'Un olor, una palabra', 'Percibe un olor cercano y descríbelo con una sola palabra.', 'Sentidos al presente', NULL, 1),
  (1, 'Textura y temperatura', 'Toca una textura (madera, tela, metal) y nota su temperatura y superficie.', 'Sentidos al presente', NULL, 1),
  (1, 'Sonido lejano', 'Busca el sonido más lejano que alcances a oír y quédate con él unos segundos.', 'Sentidos al presente', NULL, 1),
  (1, 'Un color dominante', 'Mira por la ventana 60 s y nombra el color que predomina ahora mismo.', 'Sentidos al presente', NULL, 1),
  (1, 'Hombros adelante y atrás', 'Rueda los hombros 10 veces hacia delante y 10 hacia atrás soltando rigidez.', 'Movimiento suave', NULL, 1),
  (1, 'Cuello en tres direcciones', 'Inclina la cabeza a derecha, izquierda y abajo (10 s cada una) con suavidad.', 'Movimiento suave', NULL, 1),
  (1, 'Manos despiertas', 'Abre y cierra las manos 20 veces; siente el cosquilleo activando la circulación.', 'Movimiento suave', NULL, 1),
  (1, 'Postura atenta', 'Endereza la espalda, baja hombros, afloja mandíbula y respira 3 veces así.', 'Movimiento suave', NULL, 1),
  (1, 'Sacudir y observar', 'Sacude brazos y piernas 30 s y después observa el cuerpo 30 s en quietud.', 'Movimiento suave', NULL, 1),
  (1, 'Estira gemelos', 'Apoya manos en la pared y estira cada gemelo 30 s notando el alargamiento.', 'Movimiento suave', NULL, 1),
  (1, 'Orden exprés', 'Elige un solo objeto fuera de lugar y devuélvelo a su sitio.', 'Entorno que acompaña', NULL, 1),
  (1, 'Limpieza consciente', 'Limpia una pequeña superficie (pantalla o mesa) prestando atención al gesto.', 'Entorno que acompaña', NULL, 1),
  (1, 'Luz y aire amable', 'Abre una ventana o ajusta la luz para que el ambiente te resulte más amable.', 'Entorno que acompaña', NULL, 1),
  (1, 'Vaso de agua presente', 'Bebe un vaso de agua notando su temperatura y su recorrido por el cuerpo.', 'Entorno que acompaña', NULL, 1),
  (1, 'Tocar vida verde', 'Riega una planta o acaricia una hoja con cuidado, apreciando su textura.', 'Entorno que acompaña', NULL, 1),
  (1, 'Espacio de foco', 'Guarda una sola cosa que te distrae ahora mismo y respira el vacío que queda.', 'Entorno que acompaña', NULL, 1),
  (1, 'Ponle nombre a la emoción', 'Nómbrala en una palabra (p. ej., “nervioso”, “tranquilo”) sin juzgarla.', 'Mente, emoción y gratitud', NULL, 1),
  (1, 'Intención en 7 palabras', 'Formula tu intención para la próxima hora en siete palabras o menos.', 'Mente, emoción y gratitud', NULL, 1),
  (1, 'Una gratitud de hoy', 'Piensa en una cosa por la que dar gracias hoy y sosténla unos segundos.', 'Mente, emoción y gratitud', NULL, 1),
  (1, '¿Qué necesito ahora?', 'Pregúntate eso y nómbralo con honestidad; basta con una palabra o frase corta.', 'Mente, emoción y gratitud', NULL, 1),
  (1, 'Sonrisa suave', 'Sonríe suavemente durante 30 s y percibe cómo cambia el cuerpo.', 'Mente, emoción y gratitud', NULL, 1),
  (1, 'Dos frases de estado', 'Escribe dos frases breves sobre cómo estás ahora mismo.', 'Mente, emoción y gratitud', NULL, 1),
  (1, 'Un buen deseo en silencio', 'Piensa en alguien y deséale bienestar en silencio.', 'Presencia con los demás', NULL, 1),
  (1, 'Escuchar antes de responder', 'Comprométete a dejar 10 s de escucha antes de contestar la próxima vez.', 'Presencia con los demás', NULL, 1),
  (1, 'Rostro relajado', 'Relaja mandíbula, frente y lengua para suavizar tu expresión.', 'Presencia con los demás', NULL, 1),
  (1, 'Frase amable preparada', 'Elige una frase breve que puedas usar hoy (p. ej., “Gracias por…”).', 'Presencia con los demás', NULL, 1),
  (1, 'Tono interno amigo', 'Observa cómo te hablas y cambia a un tono de buen amigo.', 'Presencia con los demás', NULL, 1),
  (1, 'Admira una cualidad', 'Nota una cosa que admiras de alguien cercano y reconócela mentalmente.', 'Presencia con los demás', NULL, 1),
  (1, 'Parpadeo consciente', 'Parpadea suavemente durante 30 s y deja descansar la vista otros 30 s.', 'Respiración & calma', NULL, 1),
  (1, 'Respira por la nariz', 'Inhala y exhala solo por la nariz durante un minuto, de forma cómoda.', 'Respiración & calma', NULL, 1),
  (1, 'Exhala con susurro', 'Exhala con un susurro suave (como “haa”) y nota cómo se relaja el pecho.', 'Respiración & calma', NULL, 1),
  (1, 'Cuenta atrás 5→1', 'Respira y cuenta mentalmente 5,4,3,2,1 con cada exhalación.', 'Respiración & calma', NULL, 1),
  (1, 'Cierra los ojos 30 s', 'Cierra los ojos medio minuto y nota 3 sensaciones internas al abrirlos.', 'Sentidos al presente', NULL, 1),
  (1, 'Busca formas redondas', 'Detecta a tu alrededor 3 objetos con bordes redondeados.', 'Sentidos al presente', NULL, 1),
  (1, 'Ritmo del entorno', 'Percibe cualquier ritmo presente (teclado, pasos, viento) sin intervenir.', 'Sentidos al presente', NULL, 1),
  (1, 'Temperatura del aire', 'Nota si el aire se siente más fresco al inhalar y más tibio al exhalar.', 'Sentidos al presente', NULL, 1),
  (1, 'Tobillos activos', 'Rota cada tobillo 10 veces en cada dirección con suavidad.', 'Movimiento suave', NULL, 1),
  (1, 'Extensión de brazos', 'Eleva brazos al inhalar y bájalos al exhalar, 6 repeticiones.', 'Movimiento suave', NULL, 1),
  (1, 'Columna larga', 'Alarga coronilla hacia arriba y coxis hacia abajo durante 60 s.', 'Movimiento suave', NULL, 1),
  (1, 'Estira muñecas', 'Con suavidad, estira la palma hacia ti y luego al revés, 15 s por lado.', 'Movimiento suave', NULL, 1),
  (1, 'Pantalla en orden', 'Cierra una pestaña o app que no necesitas ahora mismo.', 'Entorno que acompaña', NULL, 1),
  (1, 'Cables recogidos', 'Ordena un cable o accesorio suelto para despejar visualmente.', 'Entorno que acompaña', NULL, 1),
  (1, 'Aroma amable', 'Acerca una infusión, café o esencia y toma 3 respiraciones con su aroma.', 'Entorno que acompaña', NULL, 1),
  (1, 'Silla ajustada', 'Ajusta ligeramente la posición de la silla para apoyar mejor la espalda.', 'Entorno que acompaña', NULL, 1),
  (1, 'Etiqueta el pensamiento', 'Cuando surja un pensamiento, nómbralo (“planificando”, “recordando”) y suéltalo.', 'Mente, emoción y gratitud', NULL, 1),
  (1, 'Mini mantra personal', 'Repite mentalmente una frase breve que te calme (p. ej., “Estoy aquí”).', 'Mente, emoción y gratitud', NULL, 1),
  (1, 'Una micro-meta', 'Define una sola cosa pequeña que quieras hacer hoy.', 'Mente, emoción y gratitud', NULL, 1),
  (1, 'Respeto a mi ritmo', 'Reconoce: “Hoy iré a mi ritmo” y respira 3 veces con esa idea.', 'Mente, emoción y gratitud', NULL, 1),
  (1, 'Respira antes de hablar', 'Antes de tu próxima frase, toma una respiración tranquila.', 'Presencia con los demás', NULL, 1),
  (1, 'Mirada amable', 'Suaviza la mirada y la expresión facial por 60 s.', 'Presencia con los demás', NULL, 1),
  (1, 'Agradece en silencio', 'Piensa en alguien y agradécele algo concreto, en silencio.', 'Presencia con los demás', NULL, 1),
  (1, 'Espalda contra respaldo', 'Apoya la espalda y suelta los hombros para abrir el pecho.', 'Presencia con los demás', NULL, 1),
  (1, 'Tres respiraciones profundas', 'Toma tres inhalaciones amplias por la nariz y exhala largo por la boca, relajando hombros.', 'Respiración & calma', NULL, 1),
  (1, 'Bostezo consciente', 'Provoca un bostezo suave y deja que el cuerpo se afloje mientras exhalas.', 'Respiración & calma', NULL, 1),
  (1, 'Exhala en 7', 'Inhala cómodo y exhala contando hasta 7, aflojando la mandíbula.', 'Respiración & calma', NULL, 1),
  (1, 'Abdomen globo', 'Imagina que el abdomen es un globo que se infla al inhalar y se desinfla al exhalar.', 'Respiración & calma', NULL, 1),
  (1, 'Pausa tras exhalar', 'Haz una breve pausa de 1–2 s tras cada exhalación, sin forzar.', 'Respiración & calma', NULL, 1),
  (1, 'Labios fruncidos', 'Exhala con labios ligeramente fruncidos para alargar la salida del aire.', 'Respiración & calma', NULL, 1),
  (1, 'Hombros que caen', 'Con cada exhalación, deja que los hombros desciendan un poco más.', 'Respiración & calma', NULL, 1),
  (1, 'Mano en abdomen', 'Coloca una mano sobre el abdomen y siente cómo sube y baja al respirar.', 'Respiración & calma', NULL, 1),
  (1, 'Tres líneas rectas', 'Encuentra 3 líneas rectas a tu alrededor y síguelas con la mirada.', 'Sentidos al presente', NULL, 1),
  (1, 'Cinco tonos de color', 'Detecta 5 tonos de color distintos en tu entorno inmediato.', 'Sentidos al presente', NULL, 1),
  (1, 'Un detalle diminuto', 'Busca un detalle pequeño que nunca hayas notado y obsérvalo 10 s.', 'Sentidos al presente', NULL, 1),
  (1, 'Sombras que se mueven', 'Observa las sombras y su movimiento sutil durante un minuto.', 'Sentidos al presente', NULL, 1),
  (1, 'Sabor de un sorbo', 'Toma un sorbo de agua o bebida y siente su sabor y temperatura.', 'Sentidos al presente', NULL, 1),
  (1, 'Temperatura de las manos', 'Junta las manos y percibe si están frías o templadas.', 'Sentidos al presente', NULL, 1),
  (1, 'Silencio entre sonidos', 'Presta atención a los espacios de silencio entre los sonidos.', 'Sentidos al presente', NULL, 1),
  (1, 'Dos texturas distintas', 'Toca dos texturas diferentes y compara su sensación en los dedos.', 'Sentidos al presente', NULL, 1),
  (1, 'Torsión sentada', 'Sentado, gira el tronco suavemente a un lado y al otro, respirando lento.', 'Movimiento suave', NULL, 1),
  (1, 'Abrir pecho detrás', 'Entrecruza manos detrás de la espalda y abre el pecho sin forzar.', 'Movimiento suave', NULL, 1),
  (1, 'Flex y punta de pies', 'Alterna flex y punta con ambos pies 20 veces para activar la circulación.', 'Movimiento suave', NULL, 1),
  (1, 'Círculos de hombros alternos', 'Dibuja círculos con un hombro y luego con el otro, 10 veces cada uno.', 'Movimiento suave', NULL, 1),
  (1, 'Mini torsión de cuello', 'Gira la cabeza a derecha e izquierda muy suavemente, respirando.', 'Movimiento suave', NULL, 1),
  (1, 'Palmas con calor', 'Frota las palmas 30 s hasta generar calor y colócalas sobre los ojos 10 s.', 'Movimiento suave', NULL, 1),
  (1, 'Mandíbula amable', 'Abre y cierra la boca 10 veces suavemente para soltar tensión mandibular.', 'Movimiento suave', NULL, 1),
  (1, 'Respira y abre escápulas', 'Lleva codos hacia atrás al inhalar y suelta al exhalar, 6 repeticiones.', 'Movimiento suave', NULL, 1),
  (1, 'Modo no molestar 1′', 'Activa no molestar un minuto y respira el silencio elegido.', 'Entorno que acompaña', NULL, 1),
  (1, 'Limpia tus gafas', 'Limpia tus gafas o pantalla del móvil con atención suave.', 'Entorno que acompaña', NULL, 1),
  (1, 'Ventila 60 s', 'Abre una ventana un minuto y toma 3 respiraciones junto al aire fresco.', 'Entorno que acompaña', NULL, 1),
  (1, 'Recoloca el cojín', 'Ajusta cojín o respaldo para apoyar mejor la zona lumbar.', 'Entorno que acompaña', NULL, 1),
  (1, 'Apaga una luz extra', 'Si hay una luz innecesaria encendida, apágala para calmar el ambiente.', 'Entorno que acompaña', NULL, 1),
  (1, 'Agua a mano', 'Coloca una botella o vaso de agua a tu alcance para recordarte hidratarte.', 'Entorno que acompaña', NULL, 1),
  (1, 'Cable fuera de la vista', 'Oculta un cable que atraviese tu mesa para despejar visualmente.', 'Entorno que acompaña', NULL, 1),
  (1, 'Pantalla más cálida', 'Baja un punto el brillo o activa tono cálido si te sienta bien.', 'Entorno que acompaña', NULL, 1),
  (1, 'Tres palabras para ahora', 'Describe tu momento con solo tres palabras.', 'Mente, emoción y gratitud', NULL, 1),
  (1, 'Reencuadre amable', 'Toma un pensamiento tenso y dale una versión un poco más amable.', 'Mente, emoción y gratitud', NULL, 1),
  (1, 'Agradece a tu cuerpo', 'Elige una parte del cuerpo y agradécele lo que hace por ti.', 'Mente, emoción y gratitud', NULL, 1),
  (1, 'Basta por hoy (mini mantra)', 'Repite en silencio: “Por ahora, esto es suficiente”.', 'Mente, emoción y gratitud', NULL, 1),
  (1, 'Una cosa bajo control', 'Nombra una sola cosa que dependa de ti en esta hora.', 'Mente, emoción y gratitud', NULL, 1),
  (1, 'Celebra un mini logro', 'Reconoce algo pequeño que hiciste bien hoy.', 'Mente, emoción y gratitud', NULL, 1),
  (1, 'Permiso para sentir', 'Dite: “Puedo sentir lo que siento” y respira 3 veces.', 'Mente, emoción y gratitud', NULL, 1),
  (1, 'Micro visualización calmante', 'Imagina por 10 s un lugar sereno; respira dentro de esa imagen.', 'Mente, emoción y gratitud', NULL, 1),
  (5, 'Respiración coherente 5′', 'Ritmo 5/5 para calmar.', 'Respiración & calma', '["Siéntate cómodo con espalda larga.","Inhala 5 por la nariz.","Exhala 5 por la nariz.","Mantén el ritmo sin forzar.","Vuelve al conteo si te distraes."]', 1),
  (5, '4–7–8 suave', 'Patrón calmante (opcional la retención).', 'Respiración & calma', '["Mano en abdomen.","Inhala 4.","Retén 7 muy suave.","Exhala 8.","Haz 4–6 ciclos y descansa."]', 1),
  (5, 'Caja 4×4', 'Cuadrado respiratorio para ordenar.', 'Respiración & calma', '["Inhala 4.","Retén 4.","Exhala 4.","Retén 4.","Repite a ritmo cómodo."]', 1),
  (5, 'Exhalación larga 4–6', 'Alargar exhalación ayuda a soltar.', 'Respiración & calma', '["Inhala 4 por nariz.","Exhala 6 con labios fruncidos.","Siente hombros que bajan.","Mantén 5 minutos."]', 1),
  (5, 'Abdominal suave', 'Lleva el aire a la barriga.', 'Respiración & calma', '["Mano en vientre y pecho.","Inhala hacia la mano del vientre.","Exhala dejando que baje.","Permanece atento 5 minutos."]', 1),
  (5, 'Suspiro fisiológico', 'Doble inhalación nasal + exhalación larga.', 'Respiración & calma', '["Inhala corto por nariz.","Añade otra inhalación corta.","Exhala largo por boca.","Repite 5–8 veces.","Respira normal en silencio."]', 1),
  (5, 'Respirar con palabras', 'Frase “aquí/ahora” para acompañar.', 'Respiración & calma', '["Piensa “aquí” al inhalar.","Piensa “ahora” al exhalar.","Sostén 5 minutos.","Retorna si te distraes."]', 1),
  (5, 'Nasal alterna suave', 'Equilibra la atención sin retenciones.', 'Respiración & calma', '["Tapa fosa derecha e inhala por izquierda.","Cambia y exhala por derecha.","Inhala por derecha.","Cambia y exhala por izquierda.","Repite sin esforzarte."]', 1),
  (5, 'Respiración de la mano', 'Recurre a tu mano como metrónomo.', 'Respiración & calma', '["Abre la mano.","Sube por un dedo inhalando.","Baja por el mismo exhalando.","Recorre los 5 dedos dos veces."]', 1),
  (5, 'Ola de aire', 'Visualiza olas que entran y salen.', 'Respiración & calma', '["Cierra suavemente los ojos.","Imagina una ola al inhalar.","Observa cómo se retira al exhalar.","Mantén 5 minutos."]', 1),
  (5, 'Cuenta del 1 al 10', 'Juego de enfoque con respiraciones.', 'Respiración & calma', '["Cuenta “1” al exhalar.","Sigue hasta “10”.","Vuelve a “1”.","Retoma sin juicio si te pierdes.","Sostén 5 minutos."]', 1),
  (5, 'Labios fruncidos', 'Exhala por un orificio pequeño.', 'Respiración & calma', '["Inhala por nariz.","Exhala por boca con labios fruncidos.","Relaja hombros y mandíbula.","Sigue 5 minutos."]', 1),
  (5, 'Pausa tras exhalar', 'Breve pausa voluntaria.', 'Respiración & calma', '["Inhala y exhala normal.","Pausa 1–2 s tras exhalar.","Retoma cuando el cuerpo lo pida.","Mantén 5 minutos."]', 1),
  (5, 'Espalda apoyada', 'Siente cómo respira la zona dorsal.', 'Respiración & calma', '["Apoya espalda en pared/respaldo.","Nota el contacto al inhalar.","Exhala largo.","Permanece 5 minutos."]', 1),
  (5, 'Balanceo respirado', 'Mecer el cuerpo con el aire.', 'Respiración & calma', '["Balancea milímetros adelante al inhalar.","Atrás al exhalar.","Mantén coronilla larga.","Continúa 5 minutos."]', 1),
  (5, 'Trípode respiratorio', 'Abdomen, costillas y espalda.', 'Respiración & calma', '["Inhala al abdomen.","Siguiente, expande costillas.","Luego, siente la espalda.","Repite el ciclo 5 minutos."]', 1),
  (5, 'Auditar el aire', 'Escucha la respiración.', 'Respiración & calma', '["Cierra los ojos.","Escucha la inhalación.","Escucha la exhalación.","Quédate 5 minutos sin juzgar."]', 1),
  (5, '3–3–3–3 minimal', 'Versión ligera de caja.', 'Respiración & calma', '["Inhala 3.","Retén 3.","Exhala 3.","Retén 3.","10–12 ciclos suaves."]', 1),
  (5, 'Susurro “haa”', 'Soplo muy suave que libera.', 'Respiración & calma', '["Inhala por nariz.","Exhala murmullando “haa”.","Relaja garganta.","Sostén 5 minutos."]', 1),
  (5, 'Conteo atrás 20→1', 'Cuenta en cada exhalación.', 'Respiración & calma', '["Elige un número (20).","Exhala y di el número.","Desciende hasta 1.","Vuelve a 20 si te pierdes.","Mantén 5 minutos."]', 1),
  (5, 'Escucha una canción', 'Cinco minutos de escucha sin multitarea.', 'Sentidos al presente', '["Elige una canción.","Volumen moderado.","Focaliza en un instrumento 1′.","Cambia a otro 1′.","Escucha el conjunto 3′."]', 1),
  (5, 'Ventana atenta', 'Observa exterior como primera vez.', 'Sentidos al presente', '["Frente a una ventana.","Detecta 5 tonos.","Encuentra 3 formas.","Observa 1 movimiento.","Respira 3 veces."]', 1),
  (5, '5‑4‑3‑2‑1 extendido', 'Ancla amable en los sentidos.', 'Sentidos al presente', '["5 cosas que ves.","4 que oyes.","3 que sientes en la piel.","2 olores.","1 sabor."]', 1),
  (5, 'Objeto en “museo”', 'Mira un objeto como exposición.', 'Sentidos al presente', '["Elige objeto.","Describe color/forma/texture.","Imagina etiqueta e historia.","Cambia de ángulo.","Respira y suelta."]', 1),
  (5, 'Paleta del día', 'Explora matices de color.', 'Sentidos al presente', '["Halla color dominante.","Encuentra 3 tonalidades.","Busca un contraste.","Quédate 1′ con ese juego.","Nombra tu combinación favorita."]', 1),
  (5, 'Texturas en la mano', 'Explora tacto con curiosidad.', 'Sentidos al presente', '["Toca 3 superficies.","Nota temperatura/rigidez.","Compara entre dedos.","Sacude manos suave.","Respira profundo."]', 1),
  (5, 'Aromas cercanos', 'Entrena olfato amable.', 'Sentidos al presente', '["Infusión/fruta/jabón.","Inhala 3 cortas + 1 larga.","Ponle nombre.","Nota efecto (calma/activa).","Agradece el momento."]', 1),
  (5, 'Sabor con atención', 'Sorbo o bocado a cámara lenta.', 'Sentidos al presente', '["Observa antes de probar.","Toma un sorbo/bocado.","Siente textura y temperatura.","Deja que el sabor se vaya.","Respira la sensación que queda."]', 1),
  (5, 'Sonido lejano', 'Abre capas de distancia.', 'Sentidos al presente', '["Ojos cerrados opcional.","Un sonido cercano.","Uno intermedio.","Uno lejano.","Alterna 2′ entre ellos."]', 1),
  (5, 'Mirada periférica', 'Expande visión lateral.', 'Sentidos al presente', '["Mira un punto fijo.","Percibe lo lateral.","Amplía arriba/abajo.","Respira suave.","Relaja ojos al final."]', 1),
  (5, 'Describir sin adjetivos', 'Observación factual.', 'Sentidos al presente', '["Elige escena/objeto.","Usa solo sustantivos y verbos.","Evita juicios.","Observa calma mental.","Respira una vez profunda."]', 1),
  (5, 'Ritmos del entorno', 'Detecta patrones alrededor.', 'Sentidos al presente', '["Escoge un ritmo.","Acompáñalo con respiración.","Cambia a otro patrón.","Alterna y compara.","Cierra con 3 respiraciones."]', 1),
  (5, 'Cielo y luz', 'Atiende cambios de luz.', 'Sentidos al presente', '["Mira cielo o techo iluminado.","Detecta gradientes.","Busca una forma.","Quédate 1′.","Parpadea suave y cierra."]', 1),
  (5, 'Manos en reposo', 'Observa tus manos paisaje.', 'Sentidos al presente', '["Apóyalas en piernas.","Nota temperatura/peso.","Percibe pulso o micro‑movimientos.","Respira hacia ellas.","Agradece su trabajo."]', 1),
  (5, 'Lectura atenta breve', 'Lee con curiosidad suave.', 'Sentidos al presente', '["Elige un párrafo.","Subraya una idea.","Respira 3 veces con ella.","Cierra el texto y deja reposar."]', 1),
  (5, 'Fotografía mental', 'Captura escena con la mente.', 'Sentidos al presente', '["Elige encuadre.","Nombra 3 elementos delante.","Nombra 2 del fondo.","“Haz clic” mental.","Respira y suelta."]', 1),
  (5, 'Temperaturas del aire', 'Nota diferencia al inspirar/expirar.', 'Sentidos al presente', '["Respira por nariz.","Aire entra más fresco.","Sale más tibio.","Permanece 3′.","Cierra con suspiro."]', 1),
  (5, 'Sombras y contornos', 'Dibuja con la vista.', 'Sentidos al presente', '["Elige un objeto.","Recorre su borde.","Observa la sombra.","Repite con otro.","Relaja ojos y cuello."]', 1),
