  (10, 'Reencuadre por escrito', 'Transforma un pensamiento tenso en uno más amable.', 'Mente, emoción y gratitud', '["Escribe el pensamiento literal.","Cuestiona: ¿es 100% cierto?, ¿qué evidencia falta?","Redáctalo de forma más neutral/útil.","Respira 3 veces sobre la nueva versión."]', 1),
  (10, 'Auto‑compasión en 3 frases', 'Dirige amabilidad hacia ti con estructura simple.', 'Mente, emoción y gratitud', '["Mano en el pecho.","“Esto es difícil para mí”.","“No estoy solo/a; a otros también les pasa”.","“Hoy seré amable conmigo”."]', 1),
  (10, 'Aparcar preocupaciones 10′', 'Da un lugar y un momento a lo que no resolverás ahora.', 'Mente, emoción y gratitud', '["Lista 3–5 preocupaciones.","Elige una hora futura para revisarlas.","Pon un recordatorio real (calendario/nota).","Guárdalas en una “caja mental”."]', 1),
  (10, 'Rueda de valores mini', 'Clarifica qué valor te guía hoy.', 'Mente, emoción y gratitud', '["Escribe 6 valores que te importan.","Elige 1 para el día de hoy.","Anota 2 conductas pequeñas que lo encarnen.","Comprométete con una ahora."]', 1),
  (10, 'Intención elaborada de la hora', 'Define con detalle cómo quieres estar en los próximos 60′.', 'Mente, emoción y gratitud', '["Escribe una intención en ≤10 palabras.","Describe 3 señales observables de que se cumple.","Piensa 1 obstáculo y 1 plan B amable.","Coloca la nota visible."]', 1),
  (10, 'Práctica de escucha 10′', 'Entrena escuchar antes de responder.', 'Presencia con los demás', '["Imagina una conversación próxima.","Decide esperar 1–2 respiraciones antes de hablar.","Ensaya validar (“te escucho/te entiendo”).","Cierra con una frase breve y clara."]', 1),
  (10, 'Frases puente y límites', 'Prepara recursos para cuidar tu ritmo.', 'Presencia con los demás', '["Escribe 4 frases: “ahora no puedo”, “te respondo luego”, “necesito pensar”, “prefiero por escrito”.","Léelas en voz baja.","Elige 1–2 para usar hoy.","Respira y guarda la nota accesible."]', 1),
  (10, 'Agradecimiento específico', 'Ten listo un “gracias” concreto y sincero.', 'Presencia con los demás', '["Piensa en la persona y el gesto.","Escribe por qué lo valoras (2 frases).","Ensáyalo con tono cálido.","Decide cuándo decírselo hoy."]', 1),
  (10, 'Pedir ayuda con claridad', 'Ensaya pedir apoyo de forma amable y directa.', 'Presencia con los demás', '["Escribe qué necesitas y por qué.","Redáctalo en una frase breve.","Añade un “¿te parece?” o “¿podría ser?”.","Incluye un gracias real."]', 1),
  (10, 'Revisión de conversación', 'Integra aprendizaje de un intercambio reciente.', 'Presencia con los demás', '["Elige una conversación de hoy o ayer.","Anota 1 cosa que funcionó bien.","Anota 1 cosa a mejorar.","Define un micro‑paso para la próxima vez."]', 1),
  (10, 'Empatía estructurada', 'Mira desde el punto de vista del otro.', 'Presencia con los demás', '["Elige una situación con alguien cercano.","Escribe cómo la vería la otra persona.","Escribe qué podría necesitar.","Anota 1 gesto que tú sí puedes cuidar."]', 1);
